def add_score():
    global grade
    apple = input('請輸入科目:')
    if not(apple in grade):
        yee=int(input("成績:"))
        grade[apple] = yee
    else:
        print("該科目已新增了")

def remove_score():
    global grade
    apple = input('刪除某個科目的成績:')
    if apple in grade:
        grade.pop(apple)
        print('已經取消了')

def out_score():
    print(f"總平均為:{sum(grade.values())/len(grade)}")

